  async exibirAlertaFavorito(filme){
    const alert = await this.alertController.create({
      header: 'Favoritar',
      message: 'Você deseja favoritar esse filme ?',
      buttons: [
        {
          text: 'Sim, Favoritar',
          handler: () => {
            this.ExibirToast('Esse filme foi favoritado','success');
            filme.favorito = true;
          }
        }, {
          text: 'Cancelar',
          role: 'cancel',
          handler: () =>{
            this.ExibirToast('Esse filme foi desfavoritado', 'warning');
            filme.favorito = false;
          }
        }
      ]
    });

    await alert.present();
  }

  async ExibirToast(mensagem: string, cor: string) {
    const toast = await this.toastController.create({
      message: mensagem,
      color:cor,
      duration: 2000
    });
    toast.present();
  }
